import { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import CartContext from "../store/CartContext";
const Login = () => {
  const ctx = useContext(CartContext);
  const navigate = useNavigate();
  const [userInput, setUserInput] = useState({
    email: "",
    password: "",
  });
  const [emailIsTouched, setEmailIsTouched] = useState(false);
  const { email, password } = userInput;
  const emailIsValid = email.trim() !== "" && email.includes("@");
  const emailIsInvalid = !emailIsValid && emailIsTouched;
  const changeHandler = (e) => {
    setUserInput({
      ...userInput,
      [e.target.name]: e.target.value,
    });
  };
  const blurHandler = () => {
    setEmailIsTouched(true);
  };
  const formSubmitHandler = (e) => {
    e.preventDefault();
    setEmailIsTouched(true);
    if (!emailIsValid) {
      return;
    }
    navigate("/profile");
  };
  const errorCls = emailIsInvalid && "form-error";
  return (
    <div
      className="container mt-md-auto w-25 mt-lg-5"
      style={{ backgroundColor: "#f2f2f2", padding: "20px" }}
    >
      {!ctx.isLoggedIn && (
        <form onSubmit={formSubmitHandler} className={errorCls}>
          <div className="mb-3 mt-3">
            <label htmlFor="email" className="form-label">
              Email:
            </label>
            <input
              type="text"
              className="form-control"
              id="email"
              placeholder="Enter email"
              name="email"
              value={email}
              onChange={changeHandler}
              onBlur={blurHandler}
            />
            {emailIsInvalid && (
              <p className="input-error">Please enter a valid email</p>
            )}
          </div>
          <div className="mb-3">
            <label htmlFor="pwd" className="form-label">
              Password:
            </label>
            <input
              type="password"
              className="form-control"
              id="pwd"
              placeholder="Enter password"
              name="password"
              value={password}
              onChange={changeHandler}
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      )}
    </div>
  );
};
export default Login;
